import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const Api = () => {
  const [books, setBooks] = useState([]);
  const [currentlyReading, setCurrentlyReading] = useState([]);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/search-books?q=`);
        setBooks(response.data.items);
      } catch (error) {
        console.error(error);
      }
    };

    fetchBooks();
  }, []);

  const handleBookSelect = (book) => {
    if (currentlyReading.some((readingBook) => readingBook.id === book.id)) {
      setCurrentlyReading((prevCurrentlyReading) =>
        prevCurrentlyReading.filter((readingBook) => readingBook.id !== book.id)
      );
    } else {
      setCurrentlyReading((prevCurrentlyReading) => [...prevCurrentlyReading, book]);
    }
  };


    return (
        <div className="background-api">
          <h1>Currently Reading</h1>
          {Array.isArray(books) && books.length === 0 ? (
            <p>No books found.</p>
          ) : (
            <Slider dots={true} infinite={true} speed={500} slidesToShow={3} slidesToScroll={1}>
              {books && books.map((book) => (
                <div key={book.id}>
                  <img
                    src={book.volumeInfo.imageLinks.thumbnail}
                    alt={book.volumeInfo.title}
                  />
                  <p>{book.volumeInfo.title}</p>
                  <button onClick={() => handleBookSelect(book)}>
                    {currentlyReading.some((readingBook) => readingBook.id === book.id)
                      ? 'Remove from Currently Reading'
                      : 'Add to Currently Reading'}
                  </button>
                </div>
              ))}
            </Slider>
          )}
        </div>

  )
};

export default Api;
