import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Currently() {
  const [books, setBooks] = useState([]);
  const [searchTerms, setSearchTerms] = useState({});

  useEffect(() => {
    // Fonction pour récupérer les données des livres depuis l'API Google Books
    const fetchBooks = async () => {
      try {
        const response = await axios.get('https://www.googleapis.com/books/v1/volumes?q=tech'); // Remplacez 'programming' par le terme de recherche souhaité
        setBooks(response.data.items.slice(0, 3));
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    };

    // Appel de la fonction pour récupérer les données des livres
    fetchBooks();
  }, []); // Le tableau vide signifie que useEffect sera exécuté une seule fois après le rendu initial

  useEffect(() => {
    // Extrayez les IDs des livres pour initialiser les termes de recherche
    const initialBookIds = books.map((book) => book.id);

    // Initialisez les termes de recherche avec une chaîne vide pour chaque livre
    const initialSearchTerms = initialBookIds.reduce((acc, bookId) => {
      acc[bookId] = '';
      return acc;
    }, {});

    setSearchTerms(initialSearchTerms);
  }, [books]); // Utilisez le tableau de dépendances pour mettre à jour les termes de recherche lorsque les livres changent

  const handleSearchTermChange = async (i,bookId, event) => {
    setSearchTerms((prevSearchTerms) => ({
      ...prevSearchTerms,
      [bookId]: event.target.value,
    }

    ));

    try{
        console.log(books)
        const response = await axios.get(`https://www.googleapis.com/books/v1/volumes?q=${event.target.value}`)
        console.log(response.data.items[0].volumeInfo.imageLinks.thumbnail)
        console.log(books[i])
        const findBook = response.data.items[0]
        const newBook= books.splice(i,0,)
        newBook.unshift(findBook)

        setBooks()
        setBooks(...books,
          books[`${i}`]=[
              ...books[`${i}`],
              findBook])
          console.log(books)

    }catch (error){
        console.error(error)
    }
  };

  return (
    <section className="third-section">
      <article className="book-carrousel">
        {books.map((book,i) => (
          <article key={book.id}>
            <img className="bookcover" src={book.volumeInfo.imageLinks.thumbnail} alt={`Cover of ${book.volumeInfo.title}`} />
            <p className="title">{book.volumeInfo.title}</p>
            <p className="author">{book.volumeInfo.authors && book.volumeInfo.authors.join(', ')}</p>
            <p>Rating: {book.volumeInfo.averageRating || 'N/A'}</p>
            <div>
              <label htmlFor={`searchTerm-${book.id}`}>Search Book:</label>
              <input
                type="text"
                id={`searchTerm-${book.id}`}
                value={searchTerms[book.id] || ''}
                onChange={(event) => handleSearchTermChange(i,book.id, event)}
              />
            </div>
          </article>
        ))}
      </article>
    </section>
  );
}
