import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Currently() {
  const [searchTerm, setSearchTerm] = useState(''); // Terme de recherche par défaut
  const [books, setBooks] = useState([]);

  useEffect(() => {
    // Fonction pour récupérer les données des livres depuis l'API Google Books
    const fetchBooks = async () => {
      try {
        const response = await axios.get(`https://www.googleapis.com/books/v1/volumes?q=${searchTerm}`);
        setBooks(response.data.items.slice(0, 3));
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    };

    // Appel de la fonction pour récupérer les données des livres
    fetchBooks();
  }, [searchTerm]); // Mettez le terme de recherche dans le tableau de dépendances pour recharger les livres lorsque le terme change

  const handleSearchTermChange = (event) => {
    setSearchTerm(event.target.value);
  };

  return (
    <section className="third-section">
      <div>
        <label htmlFor="searchTerm">Search Books:</label>
        <input
          type="text"
          id="searchTerm"
          value={searchTerm}
          onChange={handleSearchTermChange}
        />
      </div>
      <article className="book-carrousel">
        {books.map((book) => (
          <article key={book.id}>
            <img className="bookcover" src={book.volumeInfo.imageLinks.thumbnail} alt={`Cover of ${book.volumeInfo.title}`} />
            <p className="title">{book.volumeInfo.title}</p>
            <p className="author">{book.volumeInfo.authors && book.volumeInfo.authors.join(', ')}</p>
            <p>Rating: {book.volumeInfo.averageRating || 'N/A'}</p>
          </article>
        ))}
      </article>
    </section>
  );
}
