import React, { useEffect, useState } from 'react';
import axios from 'axios';

const API_KEY = 'AIzaSyB2TdBpCvGuKZyOvJUihcRkLyZW0yBhERY' // Remplacez par votre clé API

export default function Currently() {
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get('https://www.googleapis.com/books/v1/volumes', {
          params: {
            q: searchTerm,
            key: API_KEY,
          },
        });
        setBooks(response.data.items.slice(0, 3));
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    };

    fetchBooks();
  }, [searchTerm]);

  const handleSearchTermChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearchButtonClick = () => {
    // Appeler fetchBooks ici si vous souhaitez déclencher la recherche à l'aide d'un bouton
  };

  return (
    <section className="third-section">
      <div>
        <label htmlFor="searchTerm">Search Books:</label>
        <input
          type="text"
          id="searchTerm"
          value={searchTerm}
          onChange={handleSearchTermChange}
          onBlur={handleSearchButtonClick} // Appeler fetchBooks lorsqu'un utilisateur quitte le champ de recherche
        />
        <button onClick={handleSearchButtonClick}>Search</button>
      </div>
      <article className="book-carrousel">
        {books.map((book) => (
          <article key={book.id}>
            <img className="bookcover" src={book.volumeInfo.imageLinks.thumbnail} alt={`Cover of ${book.volumeInfo.title}`} />
            <p className="title">{book.volumeInfo.title}</p>
            <p className="author">{book.volumeInfo.authors && book.volumeInfo.authors.join(', ')}</p>
            <p>Rating: {book.volumeInfo.averageRating || 'N/A'}</p>
          </article>
        ))}
      </article>
    </section>
  );
}
