import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Currently() {
  const [books, setBooks] = useState([]);
  const [searchTerms, setSearchTerms] = useState({}); // Utilisez un objet pour stocker les termes de recherche par livre

  useEffect(() => {
    // Fonction pour récupérer les données des livres depuis l'API Google Books
    const fetchBooks = async () => {
      try {
        const bookPromises = Object.values(searchTerms).map(async (term) => {
          const response = await axios.get(`https://www.googleapis.com/books/v1/volumes?q=${term}`);
          return response.data.items[0]; // Sélectionnez le premier livre de chaque recherche
        });

        const booksData = await Promise.all(bookPromises);
        setBooks(booksData);
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    };

    // Appel de la fonction pour récupérer les données des livres
    fetchBooks();
  }, [searchTerms]); // Mettez searchTerms dans le tableau de dépendances

  const handleSearchTermChange = (bookId, event) => {
    setSearchTerms((prevSearchTerms) => ({
      ...prevSearchTerms,
      [bookId]: event.target.value,
    }));
  };

  return (
    <section className="third-section">
      <article className="book-carrousel">
        {books.map((book) => (
          <article key={book.id}>
            <img className="bookcover" src={book.volumeInfo.imageLinks.thumbnail} alt={`Cover of ${book.volumeInfo.title}`} />
            <p className="title">{book.volumeInfo.title}</p>
            <p className="author">{book.volumeInfo.authors && book.volumeInfo.authors.join(', ')}</p>
            <p>Rating: {book.volumeInfo.averageRating || 'N/A'}</p>
            <div>
              <label htmlFor={`searchTerm-${book.id}`}>Search Book:</label>
              <input
                type="text"
                id={`searchTerm-${book.id}`}
                value={searchTerms[book.id] || ''}
                onChange={(event) => handleSearchTermChange(book.id, event)}
              />
            </div>
          </article>
        ))}
      </article>
    </section>
  );
}
