import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function Api() {
  const [books, setBooks] = useState([]);
  const [query, setQuery] = useState('');
  const [selectedBook, setSelectedBook] = useState(null);

  useEffect(() => {
    // Effect pour la recherche initiale (ou lorsque query change)
    const fetchBooks = async () => {
      try {
        if (query.trim() === '') {
          setBooks([]); // Si la requête est vide, réinitialiser la liste des livres
          return;
        }

        const response = await axios.get(`http://localhost:4000/search-books?q=${query}`);
        setBooks(response.data.items);
      } catch (error) {
        console.error(error);
      }
    };

    fetchBooks();
  }, [query]);

  const handleBookSelect = (book) => {
    setSelectedBook(book);
  };

  return (
    <div className="background-api">
      <h1>Currently Reading</h1>



    <section className="third-section">
    <h5> une sélection unique pour inspirer tes futures lectures</h5>
    <article className="book-carrousel">
        <article>
            <img className="bookcover" src="./images/1Q84.jpeg" alt="cover 1Q84"/>
            <p className="title">Name of the book</p>
            <p className="author">Author of the book</p>
            <p>note</p>
        </article>
        <article>
            <img className="bookcover" src="./images/COVER3.png" alt=""/>
            <p className="title">Name of the book</p>
            <p className="author">Author of the book</p>
            <p>note</p>
        </article>
        <article>
            <img className="bookcover" src="./images/COVER4.jpeg" alt=""/>
            <p className="title">Name of the book</p>
            <p className="author">Author of the book</p>
            <p>note</p>
        </article>
    </article>

    </section>

    </div>
  );
}
