import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

export default function Api() {
  const [books, setBooks] = useState([]);
  const [query, setQuery] = useState('love'); // Terme de recherche par défaut


  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/search-books?q=${query}`);
        setBooks(response.data.items);
      } catch (error) {
        console.error(error);
      }
    };

    fetchBooks();
  }, [query]);

  return (
    <div className="background-api">
      <h1>Currently Reading</h1>
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search books..."
      />
      {Array.isArray(books) && books.length === 0 ? (
        <p>No books found.</p>
      ) : (
        <Slider dots={true} infinite={true} speed={500} slidesToShow={4} slidesToScroll={1}>
          {books && books.map((book) => (
            <div key={book.id}>
              <img
                src={book.volumeInfo.imageLinks.thumbnail}
                alt={book.volumeInfo.title}
              />
              <p>{book.volumeInfo.title}</p>
            </div>
          ))}
        </Slider>
      )}
    </div>
  );

};
