import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function Api() {
  const [books, setBooks] = useState([]);
  const [query, setQuery] = useState('');
  const [selectedBook, setSelectedBook] = useState(null);

  useEffect(() => {
    // Effect pour la recherche initiale (ou lorsque query change)
    const fetchBooks = async () => {
      try {
        if (query.trim() === '') {
          setBooks([]); // Si la requête est vide, réinitialiser la liste des livres
          return;
        }

        const response = await axios.get(`http://localhost:4000/search-books?q=${query}`);
        setBooks(response.data.items);
      } catch (error) {
        console.error(error);
      }
    };

    fetchBooks();
  }, [query]);

  const handleBookSelect = (book) => {
    setSelectedBook(book);
  };

  return (
    <div className="background-api">
      <h1>Currently Reading</h1>
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search books..."
      />

      {selectedBook ? (
        <div>
          <img
            src={selectedBook.volumeInfo.imageLinks.thumbnail}
            alt={selectedBook.volumeInfo.title}
          />
          <p>{selectedBook.volumeInfo.title}</p>
          <button onClick={() => handleBookSelect(null)}>Close</button>
        </div>
      ) : (
        <ul>
          {Array.isArray(books) && books.length === 0 ? (
            <p>No books found.</p>
          ) : (
            books && books.map((book) => (
              <li key={book.id}>
                <img
                  src={book.volumeInfo.imageLinks.thumbnail}
                  alt={book.volumeInfo.title}
                  onClick={() => handleBookSelect(book)}
                  style={{ cursor: 'pointer' }}
                />
              </li>
            ))
          )}
        </ul>
      )}
    </div>
  );
}
